package com.naver;

public class Weapon {
	
	private String bow;
	
	private String sword;
	
	private String spear;
	
	private String hammer;
	
	private String lance;
	
	public Weapon() {
		// TODO Auto-generated constructor stub
		super();
		this.bow = "bow";
		this.sword = "sword";
		this.spear = "spear";
		this.hammer = "hammer";
		this.lance = "lance";
	}

	public String getBow() {
		return bow;
	}

	public void setBow(String bow) {
		this.bow = bow;
	}

	public String getSword() {
		return sword;
	}

	public void setSword(String sword) {
		this.sword = sword;
	}

	public String getSpear() {
		return spear;
	}

	public void setSpear(String spear) {
		this.spear = spear;
	}

	public String getHammer() {
		return hammer;
	}

	public void setHammer(String hammer) {
		this.hammer = hammer;
	}

	public String getLance() {
		return lance;
	}

	public void setLance(String lance) {
		this.lance = lance;
	}
	
	
	

}
